OracleXmlCommand cmd = new OracleXmlCommand("INSERT INTO mytable (id, update_date) VALUES (:seq, :sysdate)", connection);\n\ncmd.Parameters.Add("seq", OracleDbType.Int32, ParameterDirection.Input);\ncmd.Parameters["seq"].Value = "MySeq.nextval";\n\ncmd.Parameters.Add("sysdate", OracleDbType.Date, ParameterDirection.Input);\ncmd.Parameters["sysdate"].Value = "SYSDATE";\n\ncmd.XmlCommandType = OracleXmlCommandType.Insert;\ncmd.XmlData = "<mytable><id>:seq</id><update_date>:sysdate</update_date></mytable>";\n\ncmd.ExecuteNonQuery();