// Use ApexSQL SQL Complete to perform error checking\nusing ApexSql.SqlComplete;\n\npublic class SqlErrorChecker\n{\n    public void CheckErrors(string sqlCode)\n    {\n        // Create a new instance of the ApexSQL SQL Complete parser\n        var parser = new SqlParser();\n\n        // Parse the SQL code and check for errors\n        var errors = parser.Parse(sqlCode).Errors;\n\n        // Display the errors\n        foreach (var error in errors)\n        {\n            Console.WriteLine(error.Message);\n        }\n    }\n}