using System;\n\npublic static class TaskScheduler\n{\n    public static DateTime CalculateNextRunTime(DateTime startSeed, TimeSpan frequency)\n    {\n        var now = DateTime.Now;\n        var elapsedIntervals = (now - startSeed).Ticks / frequency.Ticks;\n        var nextRunTime = startSeed.AddTicks((elapsedIntervals + 1) * frequency.Ticks);\n        return nextRunTime;\n    }\n}