[HttpPost]\npublic JsonResult UploadFile(HttpPostedFileBase file)\n{\n    string tempFile = Server.MapPath("~/Uploads/");\n    long fileSize = file.ContentLength;\n    long chunkSize = 1024 * 1024; // 1MB chunks\n    long bytesRead = 0;\n    long progress = 0;\n\n    using (FileStream fs = new FileStream(tempFile + file.FileName, FileMode.Create))\n    {\n        while (bytesRead < fileSize)\n        {\n            int read = file.InputStream.Read(buffer, 0, chunkSize);\n            fs.Write(buffer, 0, read);\n            bytesRead += read;\n\n            progress = (int)((bytesRead / (double)fileSize) * 100);\n\n            // Report progress via AJAX or JSON\n            Response.Write(string.Format("{{\"progress\":{0}}}", progress));\n            Response.Flush();\n        }\n    }\n\n    return Json(new { success = true });\n}