using System.Data.SqlClient;\nusing System.Text.RegularExpressions;\n\n// Assuming dbScript is a string containing the contents of the .sql file\nstring dbScript = File.ReadAllText("your_script.sql");\n\n// Split the script into individual batches using the GO statement as a separator\nstring[] batches = Regex.Split(dbScript, @"^\s*GO\s*$", RegexOptions.IgnoreCase | RegexOptions.Multiline);\n\nusing (SqlConnection con = new SqlConnection(yourConnectionString))\n{\n    con.Open();\n    foreach (string batch in batches)\n    {\n        if (!string.IsNullOrWhiteSpace(batch))\n        {\n            using (SqlCommand cmd = new SqlCommand(batch, con))\n            {\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}