protected void Button1_Click(object sender, EventArgs e)\n{\n    // Show the progress bar\n    ScriptManager.RegisterStartupScript(this, this.GetType(), "ShowProgressBar", "document.getElementById('progressBar').style.display = 'block';", true);\n\n    // Simulate some work\n    System.Threading.Thread.Sleep(3000);\n\n    // Hide the progress bar\n    ScriptManager.RegisterStartupScript(this, this.GetType(), "HideProgressBar", "document.getElementById('progressBar').style.display = 'none';", true);\n}