// ApplicationViewDataBase.cs\npublic abstract class ApplicationViewDataBase<TModel> : ViewPage<TModel>\n{\n    public User LoggedUser { get; set; }\n}\n\n// BaseController.cs\npublic abstract class BaseController : Controller\n{\n    protected override void OnActionExecuted(ActionExecutedContext filterContext)\n    {\n        base.OnActionExecuted(filterContext);\n        var view = filterContext.Result as ViewResult;\n        if (view != null)\n        {\n            var viewData = view.ViewData;\n            var loggedUser = _yourUserService.GetLoggedUser(); // implement your own logic to get the logged user\n            viewData.Model = new ApplicationViewDataBase(viewData.Model) { LoggedUser = loggedUser };\n        }\n    }\n}\n\n// YourController.cs\npublic class YourController : BaseController\n{\n    public ActionResult Index()\n    {\n        return View();\n    }\n}\n\n// YourView.cshtml\n@model YourApplication.Models.YourModel\n@{\n    var loggedUser = ((ApplicationViewDataBase<YourModel>)Model).LoggedUser;\n    // use loggedUser as needed\n}