using System;\nusing System.IO;\n\npublic class Program\n{\n    public static string GetShortPathName(string longPath)\n    {\n        var fileInfo = new FileInfo(longPath);\n        var shortName = fileInfo.Name;\n        var shortPath = Path.Combine(Path.GetDirectoryName(longPath), shortName);\n\n        return shortPath;\n    }\n\n    public static void Main()\n    {\n        string longPath = @"C:\Program Files\Microsoft.NET\SDK\CompactFramework\v3.5\Debugger";\n        string shortPath = GetShortPathName(longPath);\n        Console.WriteLine(shortPath);\n    }\n}