public class MyCOM : ServicedComponent\n{\n    public enum MyEnumType\n    {\n        Value1, Value2, Value3\n    }\n    public MyEnumType[] MyArray { private set; get; }\n    public MyCOM()\n    {\n        MyArray = new MyEnumType[0];\n    }\n    public void AddValue(MyEnumType value)\n    {\n        MyEnumType[] newArray = new MyEnumType[MyArray.Length + 1];\n        MyArray.CopyTo(newArray, 0);\n        newArray[MyArray.Length] = value;\n        MyArray = newArray;\n    }\n}