using HtmlAgilityPack;\nusing System.Collections.Generic;\nusing System.Linq;\n\n// ...\n\nstring table = GetContents(buffer);\n\nvar doc = new HtmlDocument();\ndoc.LoadHtml(table);\n\nvar rows = doc.DocumentNode.Descendants("tr")\n    .Skip(1) // skip the header row\n    .Select(tr => new\n    {\n        Name = tr.SelectSingleNode(".//td[2]/strong/a").InnerText,\n        Email = tr.SelectSingleNode(".//td[4]/a").GetAttributeValue("href", string.Empty)\n    })\n    .ToList();\n\n// do something with the data\nforeach (var row in rows)\n{\n    Console.WriteLine($"Name: {row.Name}, Email: {row.Email}");\n}