// Define a contract for the plug-in\npublic interface IPlugin\n{\n    void DoSomething();\n}\n\n// Create a MEF container\nvar container = new CompositionContainer();\n\n// Discover and load plug-ins\ncontainer.ComposeParts();\n\n// Get all plug-ins that implement the contract\nvar plugins = container.GetExports<IPlugin>();\n\n// Use the plug-ins\nforeach (var plugin in plugins)\n{\n    plugin.DoSomething();\n}