using System.Security.Cryptography;\nusing System.Text;\n\npublic class EncryptionHelper\n{\n    public static string Encrypt(string data)\n    {\n        byte[] entropy = Encoding.UTF8.GetBytes("YourEntropyValue");\n        byte[] dataBytes = Encoding.UTF8.GetBytes(data);\n        byte[] encryptedBytes = ProtectedData.Protect(dataBytes, entropy, DataProtectionScope.LocalMachine);\n        return Convert.ToBase64String(encryptedBytes);\n    }\n\n    public static string Decrypt(string encryptedData)\n    {\n        byte[] entropy = Encoding.UTF8.GetBytes("YourEntropyValue");\n        byte[] encryptedBytes = Convert.FromBase64String(encryptedData);\n        byte[] decryptedBytes = ProtectedData.Unprotect(encryptedBytes, entropy, DataProtectionScope.LocalMachine);\n        return Encoding.UTF8.GetString(decryptedBytes);\n    }\n}