using System.Data;\nusing System.Data.OleDb;\n\n// Assuming you have a DataSet or DataTable with the data to export\nDataSet dataSet = GetDataToExport();\n\n// Create an OleDb connection to the Access database\nstring connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\Path\\To\\Your\\Access\\Database.mdb";\nOleDbConnection connection = new OleDbConnection(connectionString);\n\n// Create an OleDb command to insert data into the Access database\nOleDbCommand command = new OleDbCommand("INSERT INTO YourTableName (Column1, Column2, ...) VALUES (?, ?, ...)", connection);\n\n// Iterate through the DataSet and insert data into the Access database\nforeach (DataRow row in dataSet.Tables[0].Rows)\n{\n    command.Parameters.AddWithValue("@Column1", row["Column1"]);\n    command.Parameters.AddWithValue("@Column2", row["Column2"]);\n    // ...\n    command.ExecuteNonQuery();\n}\n\nconnection.Close();