using System.Text.RegularExpressions;\nusing System.Web;\n\npublic class HtmlEncoder\n{\n    public static string Encode(string input)\n    {\n        // Replace curly quotes with their corresponding HTML entities\n        input = Regex.Replace(input, "\u2018", "&#8216;"); // left single curly quote\n        input = Regex.Replace(input, "\u2019", "&#8217;"); // right single curly quote\n        input = Regex.Replace(input, "\u201c", "&#8220;"); // left double curly quote\n        input = Regex.Replace(input, "\u201d", "&#8221;"); // right double curly quote\n\n        // Encode the input string\n        return HttpUtility.HtmlEncode(input);\n    }\n}