public class OneBasedCollection<T> : ICollection<T>\n{\n    private readonly ICollection<T> _innerCollection;\n\n    public OneBasedCollection(ICollection<T> innerCollection)\n    {\n        _innerCollection = innerCollection;\n    }\n\n    public T this[int index]\n    {\n        get { return _innerCollection.ElementAt(index - 1); }\n        set { _innerCollection.ElementAt(index - 1) = value; }\n    }\n\n    // Implement other ICollection<T> methods accordingly\n}