using Microsoft.Practices.EnterpriseLibrary.Security;\nusing Microsoft.Practices.EnterpriseLibrary.Security.Authorization;\n\n// Create an instance of the AuthorizationManager\nIAuthorizationManager authManager = AuthorizationFactory.GetAuthorizationManager();\n\n// Get the user's identity\nIIdentity identity = Thread.CurrentPrincipal.Identity;\n\n// Get the operations for the user\nstring[] operations = authManager.GetAllowedOperations(identity);\n\n// Print the operations\nforeach (string operation in operations)\n{\n    Console.WriteLine(operation);\n}