using Microsoft.TeamFoundation.VersionControl.Client;\n\n// Get the VersionControlServer instance\nVersionControlServer vcs = new VersionControlServer("http://your-tfs-server:8080/tfs");\n\n// Get the deleted files in a specific project\nItemSet deletedItems = vcs.GetItems("$/YourProject/*", VersionSpec.Latest, RecursionType.Full, DeletedState.Deleted, ItemType.File);\n\nforeach (Item item in deletedItems.Items)\n{\n    Console.WriteLine(item.ServerItem);\n}