public class WebServiceCache\n{\n    private readonly IMemoryCache _cache;\n\n    public WebServiceCache(IMemoryCache cache)\n    {\n        _cache = cache;\n    }\n\n    public object GetCachedResult(string methodName)\n    {\n        // Check if result is cached\n        if (_cache.TryGetValue(methodName, out object cachedResult))\n        {\n            return cachedResult;\n        }\n\n        // Cache result if not found\n        var result = MyWebServiceMethod();\n        _cache.Set(methodName, result);\n        return result;\n    }\n}