using StackExchange.Redis;\n\npublic class TicketCache\n{\n    private readonly ConnectionMultiplexer _redisConnection;\n\n    public TicketCache(ConnectionMultiplexer redisConnection)\n    {\n        _redisConnection = redisConnection;\n    }\n\n    public void StartEditing(int ticketId, int userId)\n    {\n        _redisConnection.GetDatabase().HashSet(ticketId.ToString(), "userId", userId);\n    }\n\n    public int? GetEditor(int ticketId)\n    {\n        return _redisConnection.GetDatabase().HashGet<int?>(ticketId.ToString(), "userId");\n    }\n}