using System.DirectoryServices.AccountManagement;\nusing System.Security.Claims;\n\n// Create a new PrincipalContext that connects to the AD domain\nusing (var context = new PrincipalContext(ContextType.Domain, "your_domain_name"))\n{\n    // Get the current user's claims\n    var claims = ClaimsPrincipal.Current.Claims;\n\n    // Find the claim that represents the user's AD account\n    var adAccountClaim = claims.FirstOrDefault(c => c.Type == ClaimTypes.WindowsAccountName);\n\n    if (adAccountClaim != null)\n    {\n        // Get the AD account identity\n        var adAccount = adAccountClaim.Value;\n\n        // Get the user's AD groups\n        var groups = context.GetGroupsForUser(adAccount);\n\n        // Process the groups as needed\n        foreach (var group in groups)\n        {\n            Console.WriteLine(group.Name);\n        }\n    }\n}