using System.Web;\n\npublic class CacheSizeMonitor\n{\n    public static long GetCacheSize()\n    {\n        System.Web.HttpRuntime.Cache.GetCount();\n        var cache = System.Web.HttpRuntime.Cache;\n        var cacheEnum = cache.GetEnumerator();\n        long cacheSize = 0;\n\n        while (cacheEnum.MoveNext())\n        {\n            var cacheEntry = cacheEnum.Current;\n            if (cacheEntry != null)\n            {\n                var cacheItem = cacheEntry.Value;\n                if (cacheItem != null)\n                {\n                    cacheSize += cacheItem.Size;\n                }\n            }\n        }\n\n        return cacheSize;\n    }\n}