// In your controller\nusing HtmlAgilityPack;\n\n// ...\n\nvar htmlContent = "<h1>Hello World</h1>";\nvar sanitizedHtml = SanitizeHtml(htmlContent);\n\nViewData["HtmlContent"] = sanitizedHtml;\n\n// ...\n\nprivate string SanitizeHtml(string htmlContent)\n{\n    var doc = new HtmlDocument();\n    doc.LoadHtml(htmlContent);\n\n    // Remove any script or style tags\n    foreach (var node in doc.DocumentNode.Descendants("script").Concat(doc.DocumentNode.Descendants("style")))\n    {\n        node.Remove();\n    }\n\n    return doc.DocumentNode.OuterHtml;\n}