using System;\nusing System.Net.Http;\nusing System.Xml.Linq;\nusing System.Data.SqlClient;\n\nclass JobImporter\n{\n    static void Main(string[] args)\n    {\n        // Send request and parse XML response\n        var client = new HttpClient();\n        var response = client.GetAsync("https://example.com/api/jobs").Result;\n        var xml = XDocument.Parse(response.Content.ReadAsStringAsync().Result);\n\n        // Load data into SQL Server\n        var conn = new SqlConnection("Data Source=<your_server>;Initial Catalog=<your_database>;Integrated Security=True");\n        conn.Open();\n\n        foreach (var job in xml.Descendants("Job"))\n        {\n            var sql = "INSERT INTO HoldingTable (JobId, JobName, ...) VALUES (@JobId, @JobName, ...)";\n            var cmd = new SqlCommand(sql, conn);\n            cmd.Parameters.AddWithValue("@JobId", job.Element("JobId").Value);\n            cmd.Parameters.AddWithValue("@JobName", job.Element("JobName").Value);\n            // ...\n            cmd.ExecuteNonQuery();\n        }\n\n        conn.Close();\n    }\n}