using AddressParser;\n\npublic class AddressParserExample\n{\n    public static void Main()\n    {\n        var parser = new AddressParser();\n\n        // Example 1:\n        var address1 = "23 Street n. 213 new york ny";\n        var result1 = parser.Parse(address1);\n        Console.WriteLine($"Street: {result1.Street}, Number: {result1.Number}, City: {result1.City}, State: {result1.State}");\n\n        // Example 2:\n        var address2 = "new york";\n        var result2 = parser.Parse(address2);\n        Console.WriteLine($"City: {result2.City}");\n\n        // Example 3:\n        var address3 = "12345";\n        var result3 = parser.Parse(address3);\n        Console.WriteLine($"ZIP: {result3.Zip}");\n\n        // Example 4:\n        var address4 = "Auckley Fake Street New York New York";\n        var result4 = parser.Parse(address4);\n        Console.WriteLine($"Street: {result4.Street}, City: {result4.City}, State: {result4.State}");\n    }\n}