using iTextSharp.text;\nusing iTextSharp.text.pdf;\n\n// create a PDF document\nDocument document = new Document(PageSizeLETTER);\nPdfWriter.GetInstance(document, new FileStream("labels.pdf", FileMode.Create));\n\n// add labels to the document\ndocument.Open();\nfor (int i = 0; i < 10; i++) {\n  PdfPTable table = new PdfPTable(1);\n  table.TotalWidth = 100;\n  table.LockedWidth = true;\n  PdfPCell cell = new PdfPCell(new Phrase("Label " + i));\n  cell.FixedHeight = 50;\n  table.AddCell(cell);\n  document.Add(table);\n}\ndocument.Close();