@using (Html.BeginForm("TestAction", "YourControllerName", FormMethod.Post, new { id = "yourFormId" }))\n{\n    // your form fields here\n}\n\n<script type="text/javascript">\n    $(document).ready(function () {\n        $('#yourFormId').submit(function (e) {\n            e.preventDefault();\n            $.ajax({\n                type: 'POST',\n                url: $(this).attr('action'),\n                data: $(this).serialize(),\n                success: function (data) {\n                    $('#target').html(data);\n                }\n            });\n        });\n    });\n</script>