@using (Html.BeginForm("Action", "Controller", FormMethod.Get, new { id = "myForm" }))\n{\n    @Html.TextBox("textbox1")\n    <button type="submit">Go</button>\n}\n\n<script>\n    $(document).ready(function () {\n        $("#myForm").submit(function (e) {\n            e.preventDefault();\n            var form = $(this);\n            $.ajax({\n                url: form.attr("action"),\n                type: "GET",\n                data: form.serialize(),\n                success: function (data) {\n                    // handle the response\n                }\n            });\n        });\n    });\n</script>