public class Item\n{\n    public string Text { get; set; }\n    public List<Item> Children { get; set; }\n    public string Number { get; set; }\n}\n\npublic class Program\n{\n    public static void ReOrderNumbering(Item item, string parentNumber = "")\n    {\n        if (item.Children != null)\n        {\n            int i = 1;\n            foreach (var child in item.Children)\n            {\n                child.Number = string.IsNullOrEmpty(parentNumber) ? i.ToString() : parentNumber + "." + i;\n                ReOrderNumbering(child, child.Number);\n                i++;\n            }\n        }\n    }\n\n    public static void RemoveItem(Item item, string number)\n    {\n        if (item.Children != null)\n        {\n            item.Children.RemoveAll(c => c.Number == number);\n            ReOrderNumbering(item);\n        }\n    }\n\n    public static void Main()\n    {\n        var root = new Item\n        {\n            Text = "Welcome",\n            Children = new List<Item>\n            {\n                new Item\n                {\n                    Text = "Whats New",\n                    Children = new List<Item>\n                    {\n                        new Item { Text = "Gifts", Number = "2.1" },\n                        new Item { Text = "Ideas", Number = "2.2" },\n                        new Item\n                        {\n                            Text = "Others",\n                            Children = new List<Item>\n                            {\n                                new Item { Text = "Novelty", Number = "2.3.1" },\n                                new Item { Text = "Boats", Number = "2.3.2" }\n                            }\n                        },\n                        new Item { Text = "Vehicals", Number = "2.4" },\n                        new Item { Text = "Fruits", Number = "2.5" }\n                    }\n                }\n            }\n        };\n\n        RemoveItem(root, "2.1");\n        RemoveItem(root, "2.3.1");\n\n        // Print the result\n        foreach (var item in root.Children)\n        {\n            Console.WriteLine(item.Number + " " + item.Text);\n            if (item.Children != null)\n            {\n                foreach (var child in item.Children)\n                {\n                    Console.WriteLine(child.Number + " " + child.Text);\n                }\n            }\n        }\n    }\n}