using System.Runtime.InteropServices;\n\n// Assume you have a COM interface defined in your VB6 component\n[ComImport]\n[InterfaceType(ComInterfaceType.InterfaceIsIDispatch)]\npublic interface IContext\n{\n    // Your COM interface methods here\n}\n\n// Create a .NET wrapper class that implements the COM interface\npublic class ContextWrapper : IContext\n{\n    private IContext _context;\n\n    public ContextWrapper()\n    {\n        _context = (IContext)CoCreateInstance(typeof(IContext).GUID, null, CLSCTX.ALL);\n    }\n\n    // Implement the COM interface methods here\n}\n\n// In your ASP.NET code, create a new instance of the wrapper class for each request\npublic class MyPage : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        ContextWrapper context = new ContextWrapper();\n        // Use the context object here\n    }\n}