using System;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass SocketClient\n{\n    static void Main(string[] args)\n    {\n        TcpClient client = new TcpClient();\n        client.Connect("localhost", 8080); // replace with your service's host and port\n\n        // Send preamble (16 bytes)\n        byte[] preamble = new byte[] {\n            0x00, 0x01, 0x00, 0x01, 0x00, 0x00, 0x00, 0x00,\n            0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00\n        };\n        client.GetStream().Write(preamble, 0, preamble.Length);\n\n        // Send message\n        string message = "Hello, WCF service!";\n        byte[] messageBytes = Encoding.UTF8.GetBytes(message);\n        client.GetStream().Write(messageBytes, 0, messageBytes.Length);\n\n        // Receive response\n        byte[] responseBytes = new byte[1024];\n        int bytesRead = client.GetStream().Read(responseBytes, 0, responseBytes.Length);\n        string response = Encoding.UTF8.GetString(responseBytes, 0, bytesRead);\n        Console.WriteLine("Response: " + response);\n\n        client.Close();\n    }\n}