using OfficeOpenXml;\n\n// Assuming you have a DataSet or DataTable with the data to export\nDataSet dataSet = GetDataToExport();\n\n// Create an EPPlus package for the Access database\nusing (ExcelPackage package = new ExcelPackage())\n{\n    ExcelWorksheet worksheet = package.Workbook.Worksheets.Add("YourTableName");\n\n    // Iterate through the DataSet and add data to the worksheet\n    for (int i = 0; i < dataSet.Tables[0].Rows.Count; i++)\n    {\n        for (int j = 0; j < dataSet.Tables[0].Columns.Count; j++)\n        {\n            worksheet.Cells[i + 1, j + 1].Value = dataSet.Tables[0].Rows[i][j];\n        }\n    }\n\n    // Save the package to the Access database file\n    package.SaveAs(new FileInfo("C:\\Path\\To\\Your\\Access\\Database.mdb"));\n}