using HtmlAgilityPack;\n\nstring htmlSnippet = @"\nThis is a section of a blog post. It has <a href=""#"">links</a> and lists and stuff. Weee....\n<br>\n<br>\nHere's a list\n<br/>\n<br />\n<ul><li>Item 1</li><li>Item 2</li><ul>\nAnd another paragraph here...\n<br>\n<br/>\nEtc.\n";\n\nHtmlDocument doc = new HtmlDocument();\ndoc.LoadHtml(htmlSnippet);\n\n// Find all paragraphs (or elements that could be paragraphs)\nHtmlNodeCollection paragraphs = doc.DocumentNode.SelectNodes("//p | //br");\n\n// Normalize the paragraphs\nforeach (HtmlNode p in paragraphs)\n{\n    if (p.Name == "br")\n    {\n        // Replace double <br> with a single <p> tag\n        p.ParentNode.ReplaceChild(HtmlNode.CreateNode("<p>"), p);\n    }\n    else if (p.Name == "p")\n    {\n        // Add a closing tag if missing\n        if (p.NextSibling == null)\n        {\n            p.AppendChild(HtmlNode.CreateNode("</p>"));\n        }\n    }\n}\n\n// Print the normalized HTML\nConsole.WriteLine(doc.DocumentNode.OuterHtml);