public void RenameRole(string oldRoleName, string newRoleName)\n{\n    // Check if the old role exists\n    if (Roles.RoleExists(oldRoleName))\n    {\n        // Create the new role if it doesn't exist\n        if (!Roles.RoleExists(newRoleName))\n        {\n            Roles.CreateRole(newRoleName);\n        }\n\n        // Get the users in the old role\n        var usersInOldRole = Roles.GetUsersInRole(oldRoleName);\n\n        // Add the users to the new role\n        foreach (var user in usersInOldRole)\n        {\n            Roles.AddUserToRole(user, newRoleName);\n        }\n\n        // Remove the users from the old role\n        foreach (var user in usersInOldRole)\n        {\n            Roles.RemoveUserFromRole(user, oldRoleName);\n        }\n\n        // Delete the old role\n        Roles.DeleteRole(oldRoleName);\n    }\n}