var file = new FileInfo("path/to/file");\nvar content = new StreamContent(File.OpenRead(file.FullName));\ncontent.Headers.ContentLength = file.Length;\n\nvar client = new HttpClient();\nvar request = new HttpRequestMessage(HttpMethod.Post, "http://example.com/upload")\n{\n    Content = content\n};\n\nawait client.SendAsync(request, HttpCompletionOption.Streaming, new Progress<HttpProgressEventArgs>(progress =>\n{\n    // Update your progress bar here\n    progressBar.Value = (int)(progress.BytesTransferred / (double)progress.TotalBytes);\n}));