using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass DecryptFile\n{\n    static void Main(string[] args)\n    {\n        string encryptedFile = "encrypted_file";\n        string decryptedFile = "decrypted_file";\n        string password = "my_secret_password";\n\n        // Create an AES object\n        using (Aes aes = Aes.Create())\n        {\n            // Set the key and IV\n            aes.Key = Encoding.UTF8.GetBytes(password);\n            aes.IV = new byte[16]; // Use a random IV or a fixed IV\n\n            // Create a decryptor\n            ICryptoTransform decryptor = aes.CreateDecryptor(aes.Key, aes.IV);\n\n            // Decrypt the file\n            using (FileStream fs = new FileStream(encryptedFile, FileMode.Open))\n            {\n                using (FileStream dfs = new FileStream(decryptedFile, FileMode.Create))\n                {\n                    using (CryptoStream cs = new CryptoStream(fs, decryptor, CryptoStreamMode.Read))\n                    {\n                        byte[] buffer = new byte[4096];\n                        int bytesRead;\n                        while ((bytesRead = cs.Read(buffer, 0, buffer.Length)) > 0)\n                        {\n                            dfs.Write(buffer, 0, bytesRead);\n                        }\n                    }\n                }\n            }\n        }\n    }\n}