using System;\nusing System.Text;\nusing System.Windows.Forms;\n\npublic class AnsiTextBox : TextBox\n{\n    protected override void OnKeyDown(KeyEventArgs e)\n    {\n        if (e.Control && e.KeyCode == Keys.V)\n        {\n            try\n            {\n                // Get the pasted text from the Clipboard\n                string pastedText = Clipboard.GetText();\n\n                // Convert the text to ANSI\n                byte[] ansiBytes = Encoding.Default.GetBytes(pastedText);\n                string ansiText = Encoding.Default.GetString(ansiBytes);\n\n                // Insert the ANSI text into the TextBox\n                this.SelectedText = ansiText;\n\n                // Prevent the default paste operation from occurring\n                e.Handled = true;\n            }\n            catch (Exception ex)\n            {\n                MessageBox.Show(ex.Message);\n            }\n        }\n\n        base.OnKeyDown(e);\n    }\n}