using System.Security.Cryptography;\nusing System.Text;\n\npublic class SecureTokenGenerator\n{\n    private static readonly byte[] _secretKey = Encoding.UTF8.GetBytes("YourSecretKeyHere");\n\n    public static string GenerateToken(string userId, string requestUri)\n    {\n        using (var hmac = new HMACSHA256(_secretKey))\n        {\n            var data = Encoding.UTF8.GetBytes($"{userId}:{requestUri}");\n            var hash = hmac.ComputeHash(data);\n            return Convert.ToBase64String(hash);\n        }\n    }\n}