using System;\nusing System.IO;\n\npublic class ConsoleOutputCapture\n{\n    public static void CaptureConsoleOutput()\n    {\n        using (var writer = new StringWriter())\n        {\n            Console.SetOut(writer);\n            // Run the service code here...\n            var output = writer.ToString();\n            Console.SetOut(Console.Out); // Reset the console output\n            // Do something with the captured output...\n            File.WriteAllText("console_output.log", output);\n        }\n    }\n}