using AForge.Imaging.Filters;\n\n// Load the image\nBitmap image = new Bitmap("input.png");\n\n// Create a Warp filter\nWarp filter = new Warp(new[] { new PointF(0, -100), new PointF(image.Width, 0), new PointF(image.Width, image.Height), new PointF(0, image.Height) });\n\n// Apply the filter\nBitmap transformedImage = filter.Apply(image);\n\n// Save the transformed image\ntransformedImage.Save("output.png");