[DllImport("coredll.dll")]\nprivate static extern IntPtr CreateProcess(string lpApplicationName, \n    string lpCommandLine, IntPtr lpProcessAttributes, IntPtr lpThreadAttributes, \n    bool bInheritHandles, uint dwCreationFlags, IntPtr lpEnvironment, \n    string lpCurrentDirectory, IntPtr lpStartupInfo, out IntPtr lpProcessInformation);\n\nprivate const uint CREATE_NO_WINDOW = 0x08000000;\nprivate const uint CREATE_SUSPENDED = 0x00000004;\n\npublic static void StartProcessMinimized(string path, string args)\n{\n    IntPtr pInfo = IntPtr.Zero;\n    CreateProcess(path, args, IntPtr.Zero, IntPtr.Zero, false, CREATE_NO_WINDOW, IntPtr.Zero, null, IntPtr.Zero, out pInfo);\n}\n\npublic static void StartProcessHidden(string path, string args)\n{\n    IntPtr pInfo = IntPtr.Zero;\n    CreateProcess(path, args, IntPtr.Zero, IntPtr.Zero, false, CREATE_SUSPENDED, IntPtr.Zero, null, IntPtr.Zero, out pInfo);\n}