using Microsoft.Win32;\n\nnamespace NameHere\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string keyPath = @"SYSTEM\CurrentControlSet\Control\Print\Environments\Windows NT x86\";\n            string keyName = "Test123";\n            string fullPath = keyPath + keyName;\n\n            deleteRegKey(fullPath);\n        }\n\n        static void deleteRegKey(string keyName)\n        {\n            using (RegistryKey hklm = Registry.LocalMachine)\n            {\n                hklm.DeleteSubKeyTree(keyName, false);\n            }\n        }\n    }\n}