using System.Reflection;\n\n// Assuming 'pagePath' is the string value of the XAML page path\nstring pagePath = @"\Pages\MyPage.xaml";\n\n// Get the assembly that contains the XAML page\nAssembly assembly = Assembly.GetExecutingAssembly();\n\n// Get the type of the XAML page\nType pageType = assembly.GetType(pagePath.Replace(".xaml", string.Empty).Replace("\\", "."));\n\n// Create an instance of the XAML page\nobject pageInstance = Activator.CreateInstance(pageType);\n\n// You can now cast 'pageInstance' to the base class 'Page' or your custom base class\nPage page = pageInstance as Page;