protected void Page_Load(object sender, EventArgs e)\n{\n    // Create a new Label control\n    Label label = new Label();\n    label.Text = "Hello World!";\n\n    // Add the label to the PlaceHolder\n    myPlaceHolder.Controls.Add(label);\n\n    // Add a line break\n    myPlaceHolder.Controls.Add(new LiteralControl("<br />"));\n\n    // Add another paragraph\n    Label paragraph = new Label();\n    paragraph.Text = "This is a new paragraph.";\n    myPlaceHolder.Controls.Add(paragraph);\n}