using System;\nusing System.Windows.Forms;\nusing SharpDX;\nusing SharpDX.Direct2D1;\nusing SharpDX.DXGI;\n\npublic class FastDrawingForm : Form\n{\n    private Direct2D1.Factory1 _factory;\n    private RenderTarget _renderTarget;\n    private SolidColorBrush _brush;\n\n    public FastDrawingForm()\n    {\n        _factory = new Direct2D1.Factory1();\n        _renderTarget = new RenderTarget(_factory, new RenderTargetProperties(new PixelFormat(Format.B8G8R8A8_UNorm, AlphaMode.Premultiplied)));\n        _brush = new SolidColorBrush(_renderTarget, Color.LightBlue);\n    }\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        base.OnPaint(e);\n\n        _renderTarget.BeginDraw();\n        _renderTarget.Clear(Color.White);\n\n        // Draw tens of thousands of particles (colored circles)\n        for (int i = 0; i < 10000; i++)\n        {\n            _renderTarget.DrawEllipse(new Ellipse(new PointF(100, 100), 10, 10), _brush, 2);\n        }\n\n        _renderTarget.EndDraw();\n    }\n}