[HttpPost]\npublic ActionResult Vote(int pictureId)\n{\n    // Generate a unique identifier\n    var voterId = Guid.NewGuid();\n\n    // Set the cookie\n    Response.Cookies.Add(new HttpCookie("VoterId", voterId.ToString()));\n\n    // Store the vote in the database\n    var vote = new Vote { PictureId = pictureId, VoterId = voterId };\n    db.Votes.Add(vote);\n    db.SaveChanges();\n\n    return RedirectToAction("Index");\n}