// Load the assembly containing the custom permission class and attribute\nAssembly assembly = Assembly.LoadFrom("path/to/your/assembly.dll");\n\n// Get the custom permission class\nType permissionType = assembly.GetType("YourPermissionClass");\n\n// Create an instance of the custom permission class\nIPermission permission = (IPermission)Activator.CreateInstance(permissionType);\n\n// Use the custom permission instance\npermission.Demand();