using System;\nusing System.Runtime.InteropServices;\n\npublic class DisplayController\n{\n    [DllImport("user32.dll")]\n    private static extern IntPtr SendMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\n    private const uint WM_SYSCOMMAND = 0x0112;\n    private const uint SC_MONITORPOWER = 0xF170;\n\n    public static void SwitchOnDisplay()\n    {\n        SendMessage(IntPtr.Zero, WM_SYSCOMMAND, (IntPtr)SC_MONITORPOWER, (IntPtr)(-1));\n    }\n\n    public static void SwitchOffDisplay()\n    {\n        SendMessage(IntPtr.Zero, WM_SYSCOMMAND, (IntPtr)SC_MONITORPOWER, (IntPtr)2);\n    }\n}