// Server-side\n[ServiceContract(CallbackContract = typeof(ICallbackContract))]\npublic interface IServiceContract\n{\n    void GetValue();\n}\n\npublic class Service : IServiceContract\n{\n    public void GetValue()\n    {\n        var callback = OperationContext.Current.GetCallbackChannel<ICallbackContract>();\n        callback.UpdateValue("New value from server");\n    }\n}\n\n// Client-side\npublic class CallbackHandler : ICallbackContract\n{\n    public void UpdateValue(string newValue)\n    {\n        Console.WriteLine("Updated value: " + newValue);\n    }\n}\n\npublic class Client\n{\n    public static void Main(string[] args)\n    {\n        var client = new ServiceClient(new InstanceContext(new CallbackHandler()));\n        client.GetValue();\n    }\n}