using SubSonic;\nusing SubSonic.Repository;\n\n// Define your database connection\nvar connectionString = "Server=myServer;Database=myDB;User Id=myUser;Password=myPassword;";\n\n// Create a SubSonic repository\nvar repo = new SubSonicRepository(connectionString);\n\n// Define a simple model\npublic class User\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public string Email { get; set; }\n}\n\n// Use the repository to perform CRUD operations\nvar user = new User { Name = "John Doe", Email = "john.doe@example.com" };\nrepo.Add(user);\nrepo.Save();\n\nvar users = repo.All<User>();\nforeach (var u in users)\n{\n    Console.WriteLine(u.Name);\n}