public class PdfHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Get the PDF file path\n        string filePath = context.Request.PhysicalPath;\n\n        // Get the file length\n        long fileLength = new FileInfo(filePath).Length;\n\n        // Get the range request headers\n        string rangeHeader = context.Request.Headers["Range"];\n\n        // Parse the range request\n        long start = 0;\n        long end = fileLength - 1;\n        if (!string.IsNullOrEmpty(rangeHeader))\n        {\n            string[] parts = rangeHeader.Split(new[] { "=" }, StringSplitOptions.RemoveEmptyEntries);\n            if (parts.Length == 2)\n            {\n                string[] rangeParts = parts[1].Split(new[] { "-" }, StringSplitOptions.RemoveEmptyEntries);\n                start = long.Parse(rangeParts[0]);\n                end = long.Parse(rangeParts[1]);\n            }\n        }\n\n        // Send the partial content response\n        context.Response.StatusCode = 206; // Partial Content\n        context.Response.StatusDescription = "Partial Content";\n        context.Response.ContentType = "application/pdf";\n        context.Response.AddHeader("Content-Range", $"bytes {start}-{end}/{fileLength}");\n        context.Response.TransmitFile(filePath, start, end - start + 1);\n    }\n}